import"./chunk-JS3ZFT6L.js";var t="CAPOTE",o='"We walk through the deserted streets, and from time to time, a shapeless black ghost bursts forth from a door, with a large hood covering its head."',i="Brand\xE3o, Raul. As Ilhas Desconhecidas. July 1926.",s="The figure of the Mulher do Capote (Woman of the Cape), from S\xE3o Miguel in the Azores, holds deep symbolic and cultural significance. The traditional female garment \u2014 consisting of a black cloak and a hood that concealed the face \u2014 is described by Raul Brand\xE3o as enigmatic, almost spectral, imprinting the island\u2019s human landscape with a silent presence. According to the author, the capote was \u201Cmade of a magnificent cloth that lasts a lifetime,\u201D covering the body from the shoulders to the feet. The capelo, which enclosed the head, was lined with hemp to ensure firmness and was supported by a whalebone frame resting on the shoulders, giving it its distinctive shape.",h="2023",n="S\xE3o Miguel, Azores",r="Outdoor Lighting",l="Tonny Marques | Marta Gouveia",d="Year",c="Location",m="Type",g="Team",u={TITLE:t,QUOTE:o,AUTHOR:i,TEXT:s,YEAR:h,LOCATION:n,TYPE:r,TEAM:l,YEAR_LABEL:d,LOCATION_LABEL:c,TYPE_LABEL:m,TEAM_LABEL:g};export{i as AUTHOR,n as LOCATION,c as LOCATION_LABEL,o as QUOTE,l as TEAM,g as TEAM_LABEL,s as TEXT,t as TITLE,r as TYPE,m as TYPE_LABEL,h as YEAR,d as YEAR_LABEL,u as default};
